import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [contacts, setContacts] = useState([]);
  const [form, setForm] = useState({ name: "", phone: "", id: null });

  useEffect(() => {
    fetchContacts();
  }, []);

  const fetchContacts = async () => {
    const res = await axios.get("http://localhost:3001/contacts");
    setContacts(res.data);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.name || !form.phone) return alert("Please fill all fields");

    if (form.id) {
      // update
      const res = await axios.put(`http://localhost:3001/contacts/${form.id}`, {
        name: form.name,
        phone: form.phone,
      });
      setContacts(
        contacts.map((c) => (c.id === res.data.id ? res.data : c))
      );
    } else {
      // add
      const res = await axios.post("http://localhost:3001/contacts", form);
      setContacts([...contacts, res.data]);
    }

    setForm({ name: "", phone: "", id: null });
  };

  const handleDelete = async (id) => {
    await axios.delete(`http://localhost:3001/contacts/${id}`);
    setContacts(contacts.filter((c) => c.id !== id));
  };

  const handleEdit = (contact) => {
    setForm(contact);
  };

  return (
    <div className="container">
      <h2>üìá Contact List</h2>
      <form onSubmit={handleSubmit}>
        <input
          placeholder="Name"
          value={form.name}
          onChange={(e) => setForm({ ...form, name: e.target.value })}
        />
        <input
          placeholder="Phone"
          value={form.phone}
          onChange={(e) => setForm({ ...form, phone: e.target.value })}
        />
        <button type="submit">{form.id ? "Update" : "Add"}</button>
      </form>

      <ul className="list">
        {contacts.map((c) => (
          <li key={c.id}>
            <span>{c.name} - {c.phone}</span>
            <div>
              <button onClick={() => handleEdit(c)}>‚úèÔ∏è</button>
              <button onClick={() => handleDelete(c.id)}>üóëÔ∏è</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
