import React, { useEffect, useState } from "react";
import axios from "axios";
import './App.css';

function App() {
  const [contacts, setContacts] = useState([]);
  const [formData, setFormData] = useState({ name: '', phone: '', id: null });

  useEffect(() => {
    fetchContacts();
  }, []);

  const fetchContacts = async () => {
    const res = await axios.get('http://localhost:3001/contacts');
    setContacts(res.data);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (formData.id === null) {
      // إضافة
      const res = await axios.post('http://localhost:3001/contacts', {
        name: formData.name,
        phone: formData.phone
      });
      setContacts([...contacts, res.data]);
    } else {
      // تعديل
      console.log("Trying to update contact:", formData.id);

      const res = await axios.put(`http://localhost:3001/contacts/${formData.id}`, {
        name: formData.name,
        phone: formData.phone
      });
      setContacts(contacts.map(c => c.id === res.data.id ? res.data : c));
    }

    setFormData({ name: '', phone: '', id: null });
  };

  const handleDelete = async (id) => {
    await axios.delete(`http://localhost:3001/contacts/${id}`);
    setContacts(contacts.filter(c => c.id !== id));
  };

  const handleEdit = (contact) => {
    setFormData(contact);
  };

  return (
    <div className="container">
      <h2>Contact List</h2>
      <form onSubmit={handleSubmit}>
        <input
          placeholder="Name"
          value={formData.name}
          onChange={(e) => setFormData({...formData, name: e.target.value})}
        />
        <input
          placeholder="Phone"
          value={formData.phone}
          onChange={(e) => setFormData({...formData, phone: e.target.value})}
        />
        <button type="submit">{formData.id ? 'Update' : 'Add'}</button>
      </form>

      <ul>
        {contacts.map((c) => (
          <li key={c.id}>
            {c.name} - {c.phone}
            <button onClick={() => handleEdit(c)}>Edit</button>
            <button onClick={() => handleDelete(c.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
